function SendMail(email) {

  // var pdf = buffer + '';
  // console.log('pdf '+ pdf)
  var transporter = nodemailer.createTransport({
    name: 'Godaddy',
    host: "smtpout.secureserver.net",
    secure: true,
    port: 465,
    auth: {
      user: 'info@jump360.me',
      pass: 'fruitsalad@123'
    }
  });

  const mailOptions = {
    from: 'info@jump360.me', // sender address
    to: email, // list of receivers
    subject: 'Donation Payment Receipt', // Subject line
    text: "Hare Krishna!\n\n" +

      "        Thanks for your contribution.\n\n Please find attached your donation receipt.\n\n" +

      "Regards,\n" +
      "ASK Krishna?",
    attachments: [{
      filename: 'year.pdf',
      contentType: 'application/pdf',
      path: './year.pdf',
    }]
  };

  transporter.sendMail(mailOptions, function (err, info) {
    if (err) {
      console.log("======from the mail.js======" + err);
      console.log('Eror in Mail send');
    }
    else {
      console.log("======from the mail.js======" + info);
      console.log('sending Mail');
    }

  });

  // res.status(200).send('success');
  return;
}

